INSERT INTO public."Genres" ("Name", "Description") VALUES
('Tiên Hiệp', 'Thể loại tập trung vào tu luyện để trở thành tiên nhân, với yếu tố phép thuật và thần thoại.'),
('Huyền Huyễn', 'Miêu tả thế giới kỳ ảo với phép thuật, yêu quái, tu luyện và các cuộc phiêu lưu siêu nhiên.'),
('Khoa Huyễn', 'Thể loại kết hợp yếu tố khoa học viễn tưởng, công nghệ tiên tiến và các chuyến du hành không gian.'),
('Võng Du', 'Lấy bối cảnh thế giới trò chơi trực tuyến, nơi nhân vật chính trải qua các cuộc phiêu lưu và thách thức.'),
('Đô Thị', 'Miêu tả cuộc sống và những câu chuyện diễn ra trong bối cảnh thành phố hiện đại.'),
('Đồng Nhân', 'Thể loại dựa trên các tác phẩm hoặc nhân vật đã có, sáng tạo câu chuyện mới trong bối cảnh đó.'),
('Dã Sử', 'Thể loại lịch sử hư cấu, kể về các sự kiện hoặc nhân vật không có thật nhưng dựa trên bối cảnh lịch sử.'),
('Cạnh Kỹ', 'Miêu tả các cuộc thi đấu, tranh tài trong nhiều lĩnh vực khác nhau như thể thao, kỹ năng, trí tuệ.'),
('Huyền Nghi', 'Thể loại trinh thám, điều tra các vụ án bí ẩn, siêu nhiên hoặc khó giải thích.'),
('Kiếm Hiệp', 'Thể loại võ hiệp tập trung vào các cuộc phiêu lưu của những kiếm khách, với võ công và tinh thần nghĩa hiệp.'),
('Kỳ Ảo', 'Thể loại kỳ ảo với thế giới tưởng tượng, các loài sinh vật huyền bí và các cuộc phiêu lưu thần kỳ.'),
('Light Novel', 'Tiểu thuyết nhẹ nhàng, thường có hình minh họa phong cách manga, hướng đến độc giả trẻ.');